import React from 'react';
import {
  SafeAreaView,
  StyleSheet,
  ScrollView,
  View,
  Text,
  StatusBar,
  Button,
  Modal,
  TouchableOpacity,
} from 'react-native';

import {
  Header,
  LearnMoreLinks,
  Colors,
  DebugInstructions,
  ReloadInstructions,
} from 'react-native/Libraries/NewAppScreen';

import Login from './pages/Login';

let App = () => {
  let [loginVisible, setLoginVisible] = React.useState(true); // false if logged out or not signed in
  return (
    <View style={styles.container}>
      <Modal
        animationType="slide"
        transparent={false}
        visible={loginVisible}
        onRequestClose={() => {
          Alert.alert('Modal has been closed.');
        }}>
        <Login setLoginVisible={setLoginVisible} />
      </Modal>
      {/* <TouchableOpacity onPress={() => setLoginVisible(true)}>
        <Text
          style={{
            fontSize: 80,
          }}>
          Log out
        </Text>
      </TouchableOpacity> */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
});

export default App;
